import React, {Component} from 'react';

class Tasks extends Component {
    constructor(){
        super();

        this.state = {
            Tasks: [
                {
                    id: 1,
                    name: 'clean refrig',
                    detail: '',
                }
            ],
        }
    }

    handleAddEvent (e){
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
        var Task = {
            id: id,
            name: '',
            detail: '',
        }

        this.state.Tasks.push(Task);
        this.setState(this.state.Tasks);
    }

    handleTaskTable (e){
        var Task = {
            id: e.target.id,
            name: e.target.name,
            value: e.target.value,
        }

        console.log(Task)
    }

    render(){
        return (
            <TasksTable onTaskTableUpdate={this.handleTaskTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} Tasks={this.state.Tasks}/>
        )
    }
}

class TasksTable extends Component {


    render(){
        var onTaskTableUpdate = this.props.onTaskTableUpdate;
        var Tasks = this.props.Tasks.map( function(Task){
            return (
                <TaskRow onTaskTableUpdate={onTaskTableUpdate} Tasks={Task} key={Task.id}/>
            )
        })

        return (
            <div>
                <thead>
                </thead>
                <tbody>

                </tbody>
            </div>
        )
    }
}

class TaskRow extends Component {

    render(){
        <tr>
        </tr>
    }
}

export default Tasks;
