import React, {Component} from 'react';
import './TaskName.css';

class TaskName extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;
        var clickedTask = this.props.clickedTask;
        var toggleComplete = this.props.toggleComplete;

        generateMarkButton = ( clickedTask ) => {
            if (clickedTask.complete == false){
                return (
                    <button className='markBtn'>Mark Complete ✓</button>
                )
            } else if (clickedTask.complete == true){
                return (
                    <button className='completedBtn' >Mark Complete ✓</button>
                )
            }
        }

        return (
            <div>
                <header className='labelName'>
                    <label>Name</label>
                    {this.generateMarkButton}
                </header>
                <form>
                    <textarea
                        className='TaskName'
                        value={clickedTask.name}
                        placeholder='Write a task name'
                        onChange={onTaskUpdate}
                        id={clickedTask.id}
                    ></textarea>
                </form>
            </div>
        )
    }
}

export default TaskName;
