import React, {Component} from 'react';
import './List.css';

class TasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            Tasks: [
                {
                    id: 1,
                    name: 'clean the refrig',
                    description: 'Mom wants you to clean the refrig before she comes back tonight.'
                },
                {
                    id: 2,
                    name: 'cook the dinner',
                    description: 'prepare the dinner before 6pm.'
                }
            ],
        }
    }

    //Event of adding new Task in the TaskTable
    handleAddEvent = (e) => {
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
        var Task = {
            id: id,
            name: '',
            description: '',
        }

        this.state.Tasks.push(Task);
        this.setState(this.state.Tasks);
    }

    handleTask = (e) => {
        var item = {
            id: e.target.id,
            value: e.target.value,
        }

        var currentTasks = this.state.Tasks;
        var newTasks = currentTasks.map(
            function (Task){
                if (Task.id === item.id){
                    Task.name = item.value;
                }

                return Task;

            }
        );

        console.log(newTasks);
        this.setState({ Tasks: newTasks });
    }

    render(){
        return (
            <div className='TaskListDiv'>
                <button className='addBtn' onClick={this.handleAddEvent.bind(this)}>New Task</button>
                <Tasks Tasks={this.state.Tasks} onTaskUpdate={this.handleTask.bind(this)} />
            </div>  
        )
    }
}

class Tasks extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;

        var generateTasks = this.props.Tasks.map(
            function (Task){
                return (
                    <TaskRow
                        Task={Task}
                        onTaskUpdate={onTaskUpdate}
                        key={Task.id}
                    />
                )
            }
        )

        return (
            <div>
                {generateTasks}
            </div>
        )
    }
}

class TaskRow extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;

        return (
            <EditableCell
            onTaskUpdate={onTaskUpdate}
            CellData={{
                id: this.props.Task.id,
                name: this.props.Task.name,
            }}
            />
        )
    }
}

class EditableCell extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;

        return (
            <input 
                type='text'
                className='Task'
                id={this.props.CellData.id}
                value={this.props.CellData.name}
                onChange={onTaskUpdate}
            />
        )
    }
}

export default TasksList;
