import React, {Component} from 'react';
import './Task.css';

class EditableCell extends Component {
    toggleCheckbox = (e) => {
        console.log(e.target.id);
    }

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;
        var onTaskInfo = this.props.onTaskInfo;
        var Task = this.props.Task;
        var complete = Task.complete;

        if(complete ==1){
            return (
                <div>
                    <label>
                        <input type="checkbox" defaultChecked id={Task.id} onClick={this.toggleCheckbox} />
                    </label>
                <input 
                    type='text'
                    className='Task'
                    id={this.props.id}
                    value={this.props.name}
                    onChange={onTaskUpdate}
                    onClick={onTaskInfo}
                    placeholder='Enter the Task Name'
                />
                </div>
            )
        } else {
            return (
                <div>
                    <label>
                        <input type="checkbox" id={Task.id} onClick={toggleCheckbox} />
                    </label>
                <input 
                    type='text'
                    className='Task'
                    id={this.props.id}
                    value={this.props.name}
                    onChange={onTaskUpdate}
                    onClick={onTaskInfo}
                    placeholder='Enter the Task Name'
                />
                </div>
            )
        }

    }
}

