import React, {Component} from 'react';
import './List.css';

class Tasks extends Component {
    constructor(){
        super();
        this.state = {
            Task: [],
            name: [],
            detail: [],
            currentTask: '',
        }
    }

    TaskInput (event){
        var currentTask = {
            id: event.target.id,
            name: event.target.name,
            value: event.target.value,
        }
    }

    render(){
        return(
            <div>
                <input id='0'/>
                <input id='1'/>
            </div>
        )
    }
}

export default Tasks;
