import React, {Component} from 'react';
import './MarkBtn.css';

class MarkBtn extends Component {

    render(){
        const toggleTaskComplete = this.props.toggleTaskComplete;
        var complete = this.props.clickedTask.complete;

        if (complete == 0){
            return (
                <div>
                    <button className='MarkBtn' onClick={toggleTaskComplete}>Mark Complete ✔</button>
                </div>
            )
        } else if (complete == 1){
            return (
                <div>
                    <button className='CompletedBtn' onClick={toggleTaskComplete}>Mark Incomplete ✖</button>
                </div>
            )
        }
    }
}

export default MarkBtn;
