import React, {Component} from 'react';
import Tasks from './Tasks/Tasks.js';
import TaskInfo from './TaskInfo/TaskInfo.js';
import SignOut from '../SignOut/SignOut.js';
import MarkBtn from './MarkBtn/MarkBtn.js';
import FilterBox from './FilterBox/FilterBox.js';
import './TaskList.css';

class TaskList extends Component {

    constructor(props){
        super(props);

        this.state = {
            Tasks: [],

            clickedTask: {
                id: '',
                name: '',
                description: '',
                complete: '',
            },

            filterText: '',

            clickedTaskId: '',
            clickedTaskName: '',
        }
    }

    componentDidMount(){
        fetch('http://localhost:3000/Tasks')
            .then(res => res.json())
            .then(data => {
                this.setState({ Tasks: data })})
    }

    //Event of adding new Task in the Tasktable
    handleAddEvent = () => {
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString();

        fetch('http://localhost:3000/Tasks', {
            method: 'post',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                id: id,
                name: '',
                description: '',
                complete: '',
            }) 
        })
            .then(response => response.json())
            .then(data => {
                data.sort(function(a, b){
                    return a.index - b.index;
                })

                this.setState({ Tasks: data})
            })
    }

    //Event of adding new section in the Tasktable
    handleAddSection = () => {
        var id = ('section'+ Math.floor(Math.random() * 1000));

        fetch('http://localhost:3000/Tasks', {
            method: 'post',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                id: id,
                name: '---Section---',
                description: '',
                complete: '',
            }) 
        })
            .then(response => response.json())
            .then(data => {
                data.sort(function(a, b){
                    return a.index - b.index;
                })

                this.setState({ Tasks: data})
            })
    }

    //Event of editing the selected task
    handleTask = (e) => {
        var id = e.target.id;
        var name = e.target.value;

        fetch('http://localhost:3000/TasksName', {
           method: 'put',
           headers: { 'Content-Type': 'application/json' },
           body: JSON.stringify({
               id: id,
               name: name,
           })
        })
            .then(response => response.json())
            .then(data => {
                data.sort(function(a, b){
                    return a.index - b.index;
                })

                this.setState({ Tasks: data})
            })
        }



    //get the name of the selected task and show on the title bar in the task info
    getName = (e) => {
        var id = e.target.id;

        fetch('http://localhost:3000/Tasks',{
            method: 'get',
        })
            .then(response => response.json())
            .then((data) => {
                console.log(id);
                console.log(data);
                var clickedTask = data.find(
                    function(Task){
                        if (Task.id == id ){
                            return Task;
                        }
                    }
                )

                this.setState({ clickedTaskName: clickedTask.name})
            })
    }



    //get the description of the selected Task
    getDescription = (e) => {
        var id = e.target.id;
        var newTask = this.state.clickedTask;

        newTask.description = e.target.value;

        this.setState({ clickedTask: newTask });   

        fetch('http://localhost:3000/TasksDescription', {
            method: 'put',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id: this.state.clickedTask.id,
                description: this.state.clickedTask.description,
            })
         })
             .then(res => res.json())
             .then(data => {
                 console.log(data);
                 this.setState(this.state.Tasks);
                 console.log(this.state);
             })
    }


    //function of toggling the selected Task to completed or incomplete
    toggleComplete = (e) => {
        var Class = e.target.className;
        var newTask = this.state.clickedTask;

        if (newTask.complete == true){
            newTask.complete = false;
            this.setState({ clickedTask: newTask });
        } else if (newTask.complete == false){
            newTask.complete = true;
            this.setState({ clickedTask: newTask });
        }

        fetch('http://localhost:3000/TasksComplete', {
            method: 'put',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id: this.state.clickedTask.id,
                complete: this.state.clickedTask.complete,
            })
         })
             .then(res => res.json())
             .then(data => {
                 console.log(data);
                 this.setState(this.state.Tasks);
                 console.log(this.state);
             })
    }

    //function of filtering tasks by input keyword
    filterText = (e) => {
        var filterInput = e.target.value;
        this.setState({ filterText: filterInput })
    }

    render(){
        return (
            <div className='Window'>
                <div className='Project-Panel'>
                    <div className='User'></div>
                    <div className='Project-List'></div>
                </div>
                <div className='SignOut'>
                        <SignOut onRouteChange={this.props.onRouteChange}/>
                </div>
                    <div className='TaskListDiv'>
                        <header className='Tasks-header-table'>                    
                            <button className='addBtn' onClick={this.handleAddEvent.bind(this)}>New Task</button>
                            <button className='sectionBtn' onClick={this.handleAddSection.bind(this)}>New Section</button>
                            <FilterBox filterText={this.filterText}/>
                        </header>
                        <div className='Tasks'>
                            <Tasks
                                Tasks={this.state.Tasks} 
                                onTaskUpdate={this.handleTask.bind(this)} 
                                onTaskInfo={this.getName.bind(this)}
                                filterText={this.state.filterText}
                            />
                        </div>
                    </div>  
                    <div className='TaskInfo'>
                        <header className='TaskInfo-header-table'>
                            <MarkBtn clickedTask={this.state.clickedTask} toggleComplete={this.toggleComplete.bind(this)} />
                        </header>
                        <TaskInfo 
                            clickedTask={this.state.clickedTask} 
                            onTaskUpdate={this.handleTask.bind(this)} 
                            getDescription={this.getDescription.bind(this)}
                        />
                    </div>
            </div>
        )
    }
}

export default TaskList;
