import React, { Component }from 'react';
import 'tachyons';
import './Register.css';

class Register extends Component {
    constructor(){
        super();
        this.state = {
            name: '',
            email: '',
            password: '',
        }
    }

    onNameChange = (e) => {
        var name = e.target.value;
        this.setState({ name })
    }

    onEmailChange = (e) => {
        var email = e.target.value;
        this.setState({ email })
    }

    onPasswordChange = (e) => {
        var password = e.target.value;
        this.setState({ password });
    }

    render(){
        const onNameChange = this.props.onNameChange;

        return(
            <div>
                <form id='register'>
                    <fieldset className='register-format'>
                        <legend>Account</legend>
                        <div className='register-name'>
                            <label >Name</label><br />
                            <input className='regis-input' onChange={this.props.onNameChange} />
                        </div>
                        <div className='register-email'>
                            <label>Email</label><br />
                            <input className='regis-input'/>
                        </div>
                        <div className='register-pass'>
                            <label>Password</label><br />
                            <input className='regis-input'/>
                        </div>
                        <button form='register'>Sign Up</button>
                    </fieldset>       
                </form>
            </div>
        )
    }
}

export default Register;
