import React, {Component} from 'react';
import './List.css';

class TasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            Tasks: [
                {
                    id: 1,
                    name: 'clean the refrig',
                    description: 'Mom wants you to clean the refrig before she comes back tonight.'
                },
                {
                    id: 2,
                    name: 'cook the dinner',
                    description: 'prepare the dinner before 6pm.'
                }
            ],
        }
    }


    render(){

    //Event of adding new Task in the TaskTable
    handleAddEvent = () => {
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
        var Task = {
            id: id,
            name: '',
            description: '',
        }

        var currentTasks = this.state.Tasks;
        var newTasks = currentTasks.push(Task);
        this.setState({Tasks: newTasks});

        console.log(newTasks);
    }
        return (
            <div className='TaskListDiv'>
                <button className='addBtn' onClick={this.handleAddEvent}>New Task</button>
                <Tasks Tasks={this.state.Tasks}/>
            </div>  
        )
    }
}

class Tasks extends Component {

    render(){
        var generateTasks = this.props.Tasks.map(
            function (Task){
                return (
                    <input 
                        className='Task'
                        value={Task.name}
                        key={Task.id}
                    />
                )
            }
        )

        return (
            <div>
                {generateTasks}
            </div>
        )
    }
}

export default TasksList;
