import React, {Component} from 'react';
import './List.css';

class Tasks extends Component {
    constructor(props){
        super(props);

        this.state = {
            Tasks: [
                {
                    id: 1,
                    name: 'clean the refrig',
                    description: 'Mom wants you to clean the refrig before she comes back tonight.'
                }
            ],
        }
    }

    //Event of adding new Task in the TaskTable
    handleAddEvent (e){
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
        var Task = {
            id: id,
            name: '',
            description: '',
        }

        this.state.Tasks.push(Task);
        this.setState(this.state.Tasks);
    }

    //event of editing the selected task
    handleTaskTable (e){
        var item = {
            id: e.target.id,
            name: e.target.name,
            value: e.target.value,
        }

        var Tasks = this.state.Tasks;
        var newTasks = Tasks.map ( 
            function(Task){
                for (var key in Task){
                    if (key === item.name && Task.id === item.id){
                        Task[key] = item.value;
                    }
                }

                return Task;
            }
        );

        this.setState({ Tasks: newTasks })
    }

    render(){
        return (
            <div>
                <button onClick={this.handleAddEvent}>New Task</button>
                <TaskList 
                    onTaskListUpdate={this.handleTaskTable.bind(this)} 
                    onTaskAdd={this.onTaskAdd}
                    Tasks={this.state.Tasks}
                />
            </div>  
        )
    }
}

class TaskList extends Component {

    render(){
        var onTaskListUpdate = this.props.onTaskListUpdate;
        var Task = this.props.Tasks.map(
            function (Task){
                return (
                    <EditableCell onTaskListUpdate={onTaskListUpdate} Task={Task} Key={Task.id} />
                )
            }
        )

        return (
            <div>
                {Task}
            </div>
        )
    }
}

class EditableCell extends Component {

    render(){
        var onTaskListUpdate = this.props.onTaskListUpdate;

        return (
            <input onTaskListUpdate={onTaskListUpdate}/>
        )
    }
}

export default Tasks;
