import React from 'react';

const List = ({ Tasks }) => {
    return (
        Tasks.map( (Task, index) => 
            <li 
            key={index}
            onClick={getInfo()}>{Task}</li>
        )
    )
}

const getInfo = (event) => {
    console.log(event.target.value);
}

export default List;
