import React, {Component} from 'react';
import './TaskName.css';

class TaskName extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;
        var clickedTaskName = this.props.clickedTaskName;
        var clickedTaskId = this.props.clickedTaskId;

        return (
            <div className='Task-name-table'>
                <form>
                    <textarea
                        className='TaskName'
                        value={clickedTaskName}
                        placeholder='Write a task name'
                        onChange={onTaskUpdate}
                    ></textarea>
                </form>
            </div>
        )
    }
}

export default TaskName;
