import React, {Component} from 'react';

class ToDoList extends Component {

    constructor (){
        super();

        this.state = {};
        this.state.tasks = [
            { 
                id: 1,
                name: 'clean the refrig',
            },
            {
                id: 2,
                name: 'clean the kitchen',
            }
        ]

    }

    handleRowDelete (task){
        var index = this.state.tasks.indexOf(task);
        this.state.tasks.splice(index,1);
        this.setState(this.state.tasks);
    }

    handleAddEvent (event){
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);

        var task = {
            id: id,
            name: '',
        }

        this.state.tasks.push(task);
        this.setState(this.state.tasks);
    }

    handleTaskTable (event){
        var item = {
            id: event.target.id,
            name: event.target.name,
            value: event.target.value,
        };

        var tasks = this.state.tasks.slice();

        var newTasks = tasks.map( (task) => {
            for ( var key in task){
                if (key == item.name && task.id == item.id){
                    task[key] = item.value;
                }
            }

            return task;
        })

        this.setState({ tasks: newTasks})
    };

    render(){
        return (
            <div>
                <TaskTable 
                    onTaskTableUpdate={this.handleTaskTable.bind(this)}
                    onRowAdd={this.handleAddEvent.bind(this)}
                    onRowDelete={this.handleRowDelete.bind(this)}
                    tasks={this.state.tasks}
                />
            </div>    
        )
    }
}
