import React, {Component} from 'react';

class Task extends React.Component {

    constructor(props) {
      super(props);

      //  this.state.Task = [];
      this.state = {};
      this.state.filterText = "";
      this.state.Task = [

      ];

    }

    handleUserInput(filterText) {
      this.setState({filterText: filterText});
    };

    handleRowDel(Task) {
      var index = this.state.Task.indexOf(Task);
      this.state.Task.splice(index, 1);
      this.setState(this.state.Task);
    };

    handleAddEvent(evt) {
      var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
      var Task = {
        id: id,
        name: "",
        price: "",
        category: "",
        qty: 0
      }
      this.state.Task.push(Task);
      this.setState(this.state.Task);

    }

    handleTaskTable(evt) {
        var item = {
            id: evt.target.id,
            name: evt.target.name,
            value: evt.target.value
        };
        var Task = this.state.Task.slice();
        var newTask = Task.map(function(Task) {

        for (var key in Task) {
            if (key == item.name && Task.id == item.id) {
            Task[key] = item.value;
        }
      }
      return Task;
    });
      this.setState({Task:newTask});
    //  console.log(this.state.Task);
    };

    render() {

      return (
        <div>
          <SearchBar filterText={this.state.filterText} onUserInput={this.handleUserInput.bind(this)}/>
          <TaskTable onTaskTableUpdate={this.handleTaskTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} onRowDel={this.handleRowDel.bind(this)} Task={this.state.Task} filterText={this.state.filterText}/>
        </div>
      );

    }

  }
  class SearchBar extends React.Component {
    handleChange() {
      this.props.onUserInput(this.refs.filterTextInput.value);
    }
    render() {
      return (
        <div>

          <input type="text" placeholder="Search..." value={this.props.filterText} ref="filterTextInput" onChange={this.handleChange.bind(this)}/>

        </div>

      );
    }

  }

  class TaskTable extends React.Component {

    render() {
      var onTaskTableUpdate = this.props.onTaskTableUpdate;
      var rowDel = this.props.onRowDel;
      var filterText = this.props.filterText;
      var Task = this.props.Task.map(function(Task) {
        if (Task.name.indexOf(filterText) === -1) {
          return;
        }
        return (<TaskRow onTaskTableUpdate={onTaskTableUpdate} Task={Task} onDelEvent={rowDel.bind(this)} key={Task.id}/>)
      });
      return (
        <div>


        <button type="button" onClick={this.props.onRowAdd} className="btn btn-success pull-right">Add</button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Name</th>
              </tr>
            </thead>

            <tbody>
              {Task}
            </tbody>

          </table>
        </div>
      );

    }

  }

  class TaskRow extends React.Component {
    onDelEvent() {
      this.props.onDelEvent(this.props.Task);

    }
    render() {

      return (
        <tr className="eachRow">
          <EditableCell onTaskTableUpdate={this.props.onTaskTableUpdate} cellData={{
            "type": "name",
            value: this.props.Task.name,
            id: this.props.Task.id
          }}/>
          <EditableCell onTaskTableUpdate={this.props.onTaskTableUpdate} cellData={{
            type: "price",
            value: this.props.Task.price,
            id: this.props.Task.id
          }}/>
          <EditableCell onTaskTableUpdate={this.props.onTaskTableUpdate} cellData={{
            type: "qty",
            value: this.props.Task.qty,
            id: this.props.Task.id
          }}/>
          <EditableCell onTaskTableUpdate={this.props.onTaskTableUpdate} cellData={{
            type: "category",
            value: this.props.Task.category,
            id: this.props.Task.id
          }}/>
          <td className="del-cell">
            <input type="button" onClick={this.onDelEvent.bind(this)} value="X" className="del-btn"/>
          </td>
        </tr>
      );

    }

  }
  class EditableCell extends React.Component {

    render() {
      return (
        <td>
          <input type='text' name={this.props.cellData.type} id={this.props.cellData.id} value={this.props.cellData.value} onChange={this.props.onTaskTableUpdate}/>
        </td>
      );

    }

  }

  export default Task;
