import React, {Component} from 'react';
import './ToDoList.css';

class todolist extends Component {
    constructor (){
        super();
        this.state = {
            onTask: '',
            Tasks: {
            }
        }
    }

    onInputChange = (event) => {
        console.log(event.target.value)
        this.setState({ onTask: event.target.value })
    }

    addTask = () => {
        var onTask = this.state.onTask;
        console.log(onTask);
        this.state.Tasks.push(onTask);
    }


    render () {
        return (
            <div className='ToDoList'>
                <input onChange={this.onInputChange} placeholder='Enter the task'/>
                <button onClick={this.addTask}>Enter</button>
                <ul className='List'>
                </ul>

            </div>
        )
    }

}

export default todolist;
