import React, { Component }from 'react';
import 'tachyons';
import './Register.css';

class Register extends Component {
    constructor(){
        super();
        this.state = {
            name: '',
            email: '',
            password: '',
        }
    }

    onNameChange = (e) => {
        var name = e.target.value;
        this.setState({ name });
    }

    onEmailChange = (e) => {
        var email = e.target.value;
        this.setState({ email });
    }

    onPasswordChange = (e) => {
        var password = e.target.value;
        this.setState({ password });
    }

    onSubmitRegister = () => {
        console.log(this.state);
        var name = this.state.name;
        var email = this.state.email;
        var password = this.state.password;
        var joined = new Date();

        fetch('http://localhost:3000/users', {
            method: 'post',
            headers: { 'Content-Type': 'application/json',},
            body: JSON.stringify({
                name,
                email,
                password,
                joined
            })
        })
            .then(response => response.json())
            .then(response => {
                if(response == 'success'){
                    console.log('wow')
                }
            })
    }

    render(){

        return(
            <div className='register-page'>
                <form className='register-format' id='register'>
                    <fieldset>
                        <legend>Account</legend>
                        <div className='register-name'>
                            <label >Name</label><br />
                            <input className='regis-input' onChange={this.onNameChange} />
                        </div>
                        <div className='register-email'>
                            <label>Email</label><br />
                            <input className='regis-input' onChange={this.onEmailChange}/>
                        </div>
                        <div className='register-pass'>
                            <label>Password</label><br />
                            <input className='regis-input' onChange={this.onPasswordChange}/>
                        </div>
                        <p form='register' onClick={this.onSubmitRegister}>Sign Up</p>
                    </fieldset>       
                </form>
            </div>
        )
    }
}

export default Register;
