import React from 'react';

const getInfo = (e) => {
    console.log(e.target.textContent);
}

const List = ({ Tasks }) => {
    return (
        Tasks.map( (Task, index) => 
            <li 
            key={index}
            onClick={getInfo}>{Task}</li>
        )
    )
}


export default List;
