import React, {Component} from 'react';
import './TaskName.css';

class TaskName extends Component {

    render(){
        var onTaskUpdate = this.props.onTaskUpdate;
        var clickedTask = this.props.clickedTask;
        var toggleComplete = this.props.toggleComplete;


        return (
            <div>
                <form>
                    <textarea
                        className='TaskName'
                        value={clickedTask.name}
                        placeholder='Write a task name'
                        onChange={onTaskUpdate}
                        id={clickedTask.id}
                        className='Task-name-table'
                    ></textarea>
                </form>
            </div>
        )
    }
}

export default TaskName;
